// -------------------------------// Kevin T. Manley// CSE 593// -------------------------------package FlightImpl;import java.io.IOException;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.ObjectOutputStream;import java.io.ObjectInputStream;public class Flight extends ReservableItem{	public Flight( int flightNum, int flightSeats, int flightPrice )	{		super( new Integer(flightNum).toString(), flightSeats, flightPrice );	}	 // creates a deep copy of the object    public static Flight copy(Flight other) {	    Object obj = null;	    try {            // Write the object out to a byte array            ByteArrayOutputStream bos = new ByteArrayOutputStream();            ObjectOutputStream out = new ObjectOutputStream(bos);            out.writeObject(other);            out.flush();            out.close();            // Make an input stream from the byte array and read            // a copy of the object back in.            ObjectInputStream in = new ObjectInputStream(                new ByteArrayInputStream(bos.toByteArray()));            obj = in.readObject();        }	    catch(IOException e) {            e.printStackTrace();        }        catch(ClassNotFoundException cnfe) {            cnfe.printStackTrace();        }	    return (Flight)obj;    }	public String getKey()	{		return Flight.getKey( Integer.parseInt( getLocation() ) );	}	public static String getKey( int flightNum )	{		String s = "flight-" + flightNum;		return s.toLowerCase();	}	public static String getNumReservationsKey( int flightNum )	{		String s = "flight-" + flightNum + "-reservations";		return s.toLowerCase();	}}